        #{stationCode}


    union all


      select



      from pm_25_JJJ

        where  TimePoint >= #{startDateTime} and TimePoint < #{endDateTime}
        and StationCode  in

          #{stationCode}



  ) t group by


      DATE_FORMAT(TimePoint,'%Y-%m')


