select sum(优) as 优, sum(良) as 良, sum(轻度污染) as 轻度污染, sum(中度污染) as 中度污染, sum(重度污染) as 重度污染, sum(严重污染) as 严重污染 from ( select AVG(AQI)<= 50 as 优, AVG(AQI) between 51 and 100 as 良, AVG(AQI) between 101 and 150 as 轻度污染, AVG(AQI) between 151 and 200 as 中度污染, AVG(AQI) between 201 and 300 as 重度污染, AVG(AQI) > 300 as 严重污染 from ( select AQI,TimePoint from PM_25 where TimePoint >=#{startTime} and TimePoint <#{endTime} and StationCode in
#{stationCode}
union all select AQI,TimePoint from pm_25_JJJ where TimePoint >=#{startTime} and TimePoint <#{endTime} and StationCode in
#{stationCode}
) t1
group By DATE(TimePoint)
group By HOUR(TimePoint)
) as t

select
count(1) as ct,

from (
  select

  from (
