    union all
    select

    from pm_25_JJJ
    where TimePoint >=#{startTime}
    and TimePoint <#{endTime}
    and StationCode in

      #{stationCode}




      union all

      select
        AVG(AQI), Area, AVG(CO), AVG(CO_24h),
        ID, Latitude, Longitude,
        AVG(NO2), AVG(NO2_24h), AVG(O3), AVG(O3_24h),
        AVG(O3_8h), AVG(O3_8h_24h), OrderId, PM10,
        AVG(PM10_24h), AVG(PM2_5), AVG(PM2_5_24h), PositionName,
        PrimaryPollutant, Quality, AVG(SO2),
        AVG(SO2_24h), StationCode, TimePoint
      from (
        select

        from PM_25

          TimePoint >=#{startTime}
          and TimePoint <#{endTime}
          and StationCode  in

            #{stationCode}



        union all

        select

        from pm_25_JJJ

          TimePoint >=#{startTime}
          and TimePoint <#{endTime}
          and StationCode is not null and StationCode <> ''and StationCode <> 'null'
          and StationCode  in

            #{stationCode}

          and StationCode in

            #{stationCode}


      ) t2 group by StationCode

    ) as t1  group by DATE(TimePoint) ${tailSql}
  ) as t

